targets.expr:
quantile (0.5, 
    (
        max by (kubernetes_pod_name, role) (
            rate(velor_execution_block_gas_sum{ chain_name=~"$chain_name", cluster=~"$cluster", metrics_source=~"$metrics_source", namespace=~"$namespace", kubernetes_pod_name=~"$kubernetes_pod_name", role=~"$role", stage='non_storage_gas'}[$interval])
        )
        /  
        max by (kubernetes_pod_name, role) (
            rate(velor_execution_block_gas_count{ chain_name=~"$chain_name", cluster=~"$cluster", metrics_source=~"$metrics_source", namespace=~"$namespace", kubernetes_pod_name=~"$kubernetes_pod_name", role=~"$role", stage='non_storage_gas'}[$interval])
        )
    )
    / 
    (
        max by (kubernetes_pod_name, role) (
            rate(consensus_duration_sum{ chain_name=~"$chain_name", cluster=~"$cluster", metrics_source=~"$metrics_source", namespace=~"$namespace", kubernetes_pod_name=~"$kubernetes_pod_name", role=~"$role", op='execute_block'}[$interval])
        )
        / 
        max by (kubernetes_pod_name, role) (
            rate(consensus_duration_count{ chain_name=~"$chain_name", cluster=~"$cluster", metrics_source=~"$metrics_source", namespace=~"$namespace", kubernetes_pod_name=~"$kubernetes_pod_name", role=~"$role", op='execute_block'}[$interval])
        )
    )
)
