targets.expr:
sum by (git_hash) (
    max by (kubernetes_pod_name) (
        velor_validator_voting_power {chain_name=~"$chain_name", cluster=~"$cluster", metrics_source=~"$metrics_source", namespace=~"$namespace",kubernetes_pod_name=~"$kubernetes_pod_name", role=~"validator"}
    ) * 
    on (kubernetes_pod_name) group_left(git_hash) 
    (
        max by(kubernetes_pod_name, git_hash) (
            node_release_git_hash{chain_name=~"$chain_name", cluster=~"$cluster", metrics_source=~"$metrics_source", namespace=~"$namespace",kubernetes_pod_name=~"$kubernetes_pod_name", role=~"validator"}
        )
     )
) * 100 
/ 
avg(velor_total_voting_power{chain_name=~"$chain_name", cluster=~"$cluster", metrics_source=~"$metrics_source", namespace=~"$namespace",kubernetes_pod_name=~"$kubernetes_pod_name", role=~"validator"})
